/**
 * FORMSTACK FIELD EXTRACTOR - FINAL
 * 
 * Simplified: No YOUR_INTERNAL_LABEL column, YOUR_MERGE_FIELD pre-populated
 */

(function() {
  console.log('FORMSTACK FIELD EXTRACTOR');
  console.log('=========================');

  const formStructure = [];
  const processedFields = new Set();
  let fieldIndex = 0;

  const form = document.querySelector('form, .fsForm');
  if (!form) {
    console.error('Could not find form element');
    return;
  }

  const sections = form.querySelectorAll('.fsSection');
  console.log('Found ' + sections.length + ' sections');

  const sectionContainers = sections.length > 0 ? sections : [form];

  sectionContainers.forEach((sectionElement) => {
    // Get section heading (even if CSS hidden)
    const sectionHeading = sectionElement.querySelector('.fsSectionHeading, .fsSectionHeader');
    if (!sectionHeading) return; // Skip sections without headings

    // Get text content even if element is hidden with CSS
    const sectionName = sectionHeading.textContent.trim();
    if (!sectionName) return; // Skip empty sections

    console.log('\nSection: ' + sectionName);

    // Find all field cells in this section
    const fieldCells = sectionElement.querySelectorAll('.fsFieldCell');

    fieldCells.forEach((fieldCell) => {
      // Find the input/select/textarea
      const input = fieldCell.querySelector('input:not([type="hidden"]):not([type="submit"]):not([type="button"]), select, textarea') ||
                    fieldCell.nextElementSibling?.querySelector('input:not([type="hidden"]):not([type="submit"]):not([type="button"]), select, textarea');

      if (!input) return;

      // Skip if already processed
      const fieldKey = input.id || input.name;
      if (!fieldKey || processedFields.has(fieldKey)) return;

      const fieldType = input.type || input.tagName.toLowerCase();

      // For radio/checkbox groups, only process once per group
      if (fieldType === 'radio' || fieldType === 'checkbox') {
        const groupName = input.name;
        if (processedFields.has(groupName)) return;
        processedFields.add(groupName);
      } else {
        processedFields.add(fieldKey);
      }

      // Get field label - extract just the label text, not all the content
      let fieldLabel = '';

      // Try to get the label from fsLabel class specifically
      const labelElement = fieldCell.querySelector('.fsLabel');
      if (labelElement) {
        // Get only the direct text of the label, not child elements
        fieldLabel = Array.from(labelElement.childNodes)
          .filter(node => node.nodeType === Node.TEXT_NODE)
          .map(node => node.textContent.trim())
          .join(' ')
          .replace(/\*/g, '')
          .replace(/Required field$/i, '')
          .trim();
      }

      // If still empty, use the fieldCell's own text (first line only)
      if (!fieldLabel || fieldLabel.length < 2) {
        const cellText = fieldCell.textContent.trim();
        const firstLine = cellText.split('\n')[0].trim();
        fieldLabel = firstLine
          .replace(/\*/g, '')
          .replace(/Required field$/i, '')
          .trim();
      }

      // Last resort
      if (!fieldLabel || fieldLabel.length < 2) {
        fieldLabel = 'UNLABELED_FIELD_' + fieldIndex;
      }

      fieldIndex++;

      // Generate clean names
      const cleanSectionName = sectionName
        .toUpperCase()
        .replace(/[^A-Z0-9]+/g, '_')
        .replace(/^_+|_+$/g, '')
        .substring(0, 30);

      const cleanFieldName = fieldLabel
        .toUpperCase()
        .replace(/[^A-Z0-9]+/g, '_')
        .replace(/^_+|_+$/g, '')
        .substring(0, 30);

      const suggestedInternalLabel = (cleanSectionName + '_' + cleanFieldName).substring(0, 64);

      console.log('  ' + fieldLabel + ' -> ' + suggestedInternalLabel);

      // Get field info
      const isRequired = input.required || 
                        input.hasAttribute('required') || 
                        input.closest('.fsRowBody, .fsFieldRow, .fsRow, .fsSection')?.querySelector('.fsRequiredMarker, .fsRequired') !== null;

      const placeholder = input.placeholder || '';

      // Get options
      let options = '';
      if (input.tagName === 'SELECT') {
        const opts = Array.from(input.options)
          .filter(opt => opt.value)
          .map(opt => opt.textContent.trim());
        options = opts.join('; ');
      } else if (fieldType === 'radio' || fieldType === 'checkbox') {
        const groupName = input.name;
        const allOptions = form.querySelectorAll('input[name="' + groupName + '"]');
        const opts = Array.from(allOptions).map(opt => {
          const optLabel = opt.closest('label, .fsOptionLabel')?.textContent.trim() || '';
          return optLabel;
        }).filter(Boolean);
        options = opts.join('; ');
      }

      // Store field data
      formStructure.push({
        fieldNumber: fieldIndex,
        sectionName: sectionName,
        fieldLabel: fieldLabel,
        fieldType: fieldType,
        fieldId: input.id || '',
        fieldName: input.name || '',
        isRequired: isRequired ? 'YES' : 'NO',
        placeholder: placeholder,
        options: options,
        suggestedInternalLabel: suggestedInternalLabel,
        yourMergeField: suggestedInternalLabel, // Pre-populated with suggested label
        notes: ''
      });
    });
  });

  console.log('\n=========================');
  console.log('Extracted ' + fieldIndex + ' fields');

  // GENERATE CSV
  const csvRows = [
    [
      'Field #',
      'Section Name',
      'Field Label',
      'Type',
      'Required',
      'Options/Choices',
      'Field ID',
      'Field Name',
      'SUGGESTED_INTERNAL_LABEL',
      'YOUR_MERGE_FIELD',
      'Notes'
    ].join(',')
  ];

  formStructure.forEach(field => {
    csvRows.push([
      field.fieldNumber,
      '"' + field.sectionName + '"',
      '"' + field.fieldLabel + '"',
      field.fieldType,
      field.isRequired,
      '"' + field.options + '"',
      '"' + field.fieldId + '"',
      '"' + field.fieldName + '"',
      '"' + field.suggestedInternalLabel + '"',
      '"' + field.yourMergeField + '"',
      '"' + field.notes + '"'
    ].join(','));
  });

  const csv = csvRows.join('\n');

  // Download CSV
  const blob = new Blob([csv], { type: 'text/csv' });
  const url = URL.createObjectURL(blob);
  const link = document.createElement('a');
  link.href = url;
  link.download = 'formstack-fields.csv';
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);

  console.log('\nDownloaded: formstack-fields.csv');

  // ALSO GENERATE JSON
  const jsonBlob = new Blob([JSON.stringify(formStructure, null, 2)], { type: 'application/json' });
  const jsonUrl = URL.createObjectURL(jsonBlob);
  const jsonLink = document.createElement('a');
  jsonLink.href = jsonUrl;
  jsonLink.download = 'formstack-fields.json';
  document.body.appendChild(jsonLink);
  jsonLink.click();
  document.body.removeChild(jsonLink);

  console.log('Also downloaded: formstack-fields.json');

  return formStructure;
})();
